mapper.select:
select
id, username, password, sex, deleted, update_time, create_time
from t_user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and sex = #{sex}


        and deleted = #{deleted}


        and update_time = #{updateTime}


        and create_time = #{createTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and sex = #{sex}


        and deleted = #{deleted}


        and update_time = #{updateTime}


        and create_time = #{createTime}
